      if (a !== event.target.id) args[a].isChecked = false
      else args[a].isChecked = true
    }
    updateArgs(args);
  };

  return (
  <form onChange={handle}>
    <RadioButton {...args[0]} />
    <RadioButton {...args[1]} />
  </form>
)};

export const Primary = Template.bind();
