  const [_, updateArgs] = useArgs();

  const handle = (event) => {
    for (let a = 0; a < args.length; a++){
      if (a !== event.target.id) args[a].isChecked = false
      else args[a].isChecked = true
    }
    updateArgs(args);
  };

  return (
  <form onChange={handle}>
    <RadioButton {...args[0]} />
    <RadioButton {...args[1]} />
  </form>
)};

